CREATE DATABASE posts;
CREATE SCHEMA public;
CREATE TABLE offers (
    id          BIGSERIAL NOT NULL PRIMARY KEY,
    item        TEXT NOT NULL,
    account     TEXT NOT NULL,
    nationality TEXT,
    description TEXT,
    price       MONEY NOT NULL,
    start_date  DATE,
    end_date    DATE,
    delivery    BOOLEAN NOT NULL,
    pickup      BOOLEAN NOT NULL,
    location    TEXT,
    ingredients TEXT[]
    );

